// Cálculo base atualizado
const despesasFixasMensais = 1800;
const horasPorMes = 120;
const lucroDesejado = 0.10;

const custoHora = despesasFixasMensais / horasPorMes;
const valorHoraComLucro = custoHora / (1 - lucroDesejado);
const valorDia = valorHoraComLucro * 6;

const multiplicadoresComplexidade = {
  baixa: 0.5,
  media: 1.0,
  alta: 1.5,
};

const servicos = {
  design: [
    { nome: "Criação de Cards (6 unid)", dias: 2, tipo: "online", complexidade: "baixa", descricao: "Artes visuais para redes sociais (sem gestão de conteúdo)" },
    { nome: "Design de interfaces/apresentações", dias: 3, tipo: "online", complexidade: "media", descricao: "Layouts de telas e materiais como pitchs e relatórios visuais." },
    { nome: "Direção de arte para produtos", dias: 6, tipo: "online", complexidade: "alta", descricao: "Criação de identidade visual adaptada a um produto ou serviço." },
    { nome: "Manual de marca e aplicações visuais", dias: 8, tipo: "online", complexidade: "alta", descricao: "Guia de uso da identidade visual e aplicações práticas (cores, fontes, usos, mockups)." },
    { nome: "Desdobramento 360", dias: 6, tipo: "online", complexidade: "alta", descricao: "Adaptação da identidade visual em diversos formatos e canais. Máximo 10 peças." },
    { nome: "Ilustração digital isométrica", dias: 4, tipo: "online", complexidade: "alta", descricao: "Criação de ilustração vetorial com perspectiva isométrica. Máximo uma peça." }
  ],

  marketing: [
    { nome: "Visual Merchandising", dias: 15, tipo: "offline", complexidade: "media", descricao: "Planejamento, execução visual para pontos de venda físicos, criação de placas e confecção." },
    { nome: "Gestão de Redes (sem design)", dias: 5, tipo: "online", complexidade: "media", descricao: "Planejamento e publicação de conteúdo textual, calendário e interação." },
    { nome: "Estratégia de marketing digital", dias: 7, tipo: "online", complexidade: "alta", descricao: "Análise de mercado, concorrência e plano de ações online." },
    { nome: "Estratégia de inbound marketing", dias: 10, tipo: "online", complexidade: "alta", descricao: "Definição de conteúdo atrativo, jornada do cliente, funil de vendas." },
    { nome: "Otimização do Google Meu Negócio", dias: 3, tipo: "offline", complexidade: "media", descricao: "Ajuste de informações, uso de palavras-chave, postagens e análise local." },
    { nome: "Segmentação de público e persona", dias: 5, tipo: "online", complexidade: "media", descricao: "Estudo de perfil de cliente ideal com base em dados e comportamento." },
    { nome: "Landing Page", dias: 3, tipo: "online", complexidade: "alta", descricao: "Página de captura otimizada com foco em conversão." },
    { nome: "Análise de CRM", dias: 3, tipo: "online", complexidade: "media", descricao: "Leitura estratégica de dados de relacionamento com clientes." },
    { nome: "Consultoria Estratégica", dias: 7, tipo: "online", complexidade: "alta", descricao: "Aconselhamento para construção de posicionamento e ações de mercado." },
    { nome: "Campanha de Marketing", dias: 3, tipo: "online", complexidade: "alta", descricao: "Criação de uma campanha completa com peças, mensagens e cronograma." },
    { nome: "Pesquisa de Mercado", dias: 90, tipo: "online", complexidade: "alta", descricao: "Pesquisa de mercado com análise de público, concorrência e tendências." },
  ]
};


// Botões de popup
document.querySelectorAll(".abrirPopupBtn").forEach(btn => {
  btn.onclick = () => {
    document.getElementById("popupOrcamento").style.display = "flex";
    renderizarCategorias();
  };
});

document.getElementById("fecharPopup").onclick = () => {
  document.getElementById("popupOrcamento").style.display = "none";
};

function renderizarCategorias() {
  const container = document.getElementById("categoriasContainer");
  container.innerHTML = "";

  Object.keys(servicos).forEach(cat => {
    const div = document.createElement("div");
    div.className = "balao";
    div.textContent = cat.charAt(0).toUpperCase() + cat.slice(1);

    div.onclick = () => {
      div.classList.toggle("selecionado");
      atualizarServicos();
    };

    container.appendChild(div);
  });
}

function atualizarServicos() {
  const selecionadas = [...document.querySelectorAll(".balao.selecionado")].map(el =>
    el.textContent.toLowerCase()
  );

  const container = document.getElementById("servicosContainer");
  const rodape = document.querySelector(".rodape-popup");
  const temServicos = selecionadas.some(cat => servicos[cat].length > 0);
  rodape.style.display = temServicos ? "flex" : "none";

  const selecionadosAntigos = {};
  document.querySelectorAll(".servico:checked").forEach(el => {
    selecionadosAntigos[el.value] = true;
  });

  container.innerHTML = "";

  if (selecionadas.length === 0) {
    container.innerHTML = "<p>Selecione ao menos uma categoria.</p>";
    return;
  }

  selecionadas.forEach(cat => {
    const grupo = document.createElement("div");
    grupo.style.marginBottom = "16px";

    const titulo = document.createElement("h4");
    titulo.textContent = cat.charAt(0).toUpperCase() + cat.slice(1);
    grupo.appendChild(titulo);

    servicos[cat].forEach(s => {
      const checkbox = document.createElement("input");
      checkbox.type = "checkbox";
      checkbox.className = "servico";
      checkbox.dataset.dias = s.dias;
      checkbox.dataset.tipo = s.tipo;
      checkbox.dataset.complexidade = s.complexidade;
      checkbox.dataset.descricao = s.descricao;
      checkbox.value = s.nome;
      checkbox.id = `chk-${cat}-${s.nome}`;

      let valor = s.dias * valorDia;
valor *= multiplicadoresComplexidade[s.complexidade] || 1;
if (s.tipo === "offline") valor *= 1.02;

// Arredonda apenas se não for "redondo" na 1ª casa decimal
let valor1casa = Math.round(valor * 10) / 10;

if (valor !== valor1casa) {
  valor = Math.ceil(valor * 10) / 10;
}

// Atribui o valor final formatado com 2 casas decimais
checkbox.dataset.valor = valor.toFixed(2);


      if (selecionadosAntigos[s.nome]) {
        checkbox.checked = true;
      }

      const label = document.createElement("label");
      label.className = "balao produto";
      label.htmlFor = checkbox.id;
      label.textContent = `${s.nome} (${s.dias} dia${s.dias > 1 ? "s" : ""}, ${s.complexidade})`;

      if (checkbox.checked) {
        label.classList.add("selecionado");
      }

      checkbox.addEventListener("change", () => {
        label.classList.toggle("selecionado", checkbox.checked);
      });

      grupo.appendChild(checkbox);
      grupo.appendChild(label);
    });

    container.appendChild(grupo);
  });

  document.getElementById("btnCalcular").onclick = calcularOrcamento;
}

function calcularOrcamento() {
  const selecionados = document.querySelectorAll(".servico:checked");
  let totalDias = 0;
  let custo = 0;

  selecionados.forEach(el => {
    const valor = parseFloat(el.dataset.valor);
    const dias = parseInt(el.dataset.dias);
    custo += valor;
    totalDias += dias;
  });

  const resultado = document.getElementById("resultado");
  resultado.innerHTML = `
    <p><strong>Prazo de Entrega:</strong> ${totalDias} dias     |      <strong>R$ Investimento:</strong> R$ ${custo.toFixed(2)}</p>
    <p></p>
  `;

  document.getElementById("gerarPDF").style.display = "inline-block";
}

// Geração do PDF com logo JPG ou PNG e layout organizado
  // IMAGEM LOGO
  
  const logoBase64 = 'data:image/jpeg;base64,';


  document.getElementById("gerarPDF").addEventListener("click", () => {
    const { jsPDF } = window.jspdf;
    const doc = new jsPDF();
    const dataAtual = new Date().toLocaleString("pt-BR");
    let y = 10;
    const pageHeight = doc.internal.pageSize.height;
  
    const novaPaginaSeNecessario = (alturaExtra = 10) => {
      if (y + alturaExtra > pageHeight - 10) {
        doc.addPage();
        y = 10;
      }
    };
  
    try {
      doc.addImage(logoBase64, 'JPEG', 10, y, 25, 25);
      y += 40;
    } catch (e) {
      console.error("Erro ao adicionar logo:", e);
      y += 10;
    }
  
    doc.setFont("helvetica", "bold");
    doc.setFontSize(18);
    doc.setTextColor(54, 53, 53);
    doc.text("Orçamento de Prestação de Serviços por Dan Schaidt", 10, y);
    y += 10;
  
    doc.setDrawColor(72, 231, 249);
    doc.line(10, y, 200, y);
    y += 5;
  
    doc.setFontSize(12);
    doc.setTextColor(0);
    doc.text("Validade: 3 dias corridos", 10, y);
    y += 8;
  
    const servicosSelecionados = document.querySelectorAll(".servico:checked");
  
    if (servicosSelecionados.length > 0) {
      novaPaginaSeNecessario(20);
      doc.setFillColor(224, 224, 224);
      doc.rect(10, y, 190, 8, "F");
      doc.setTextColor(0);
      doc.text("Serviço", 12, y + 6);
      doc.text("Dias", 100, y + 6);
      doc.text("Valor", 150, y + 6);
      y += 14;
  
      doc.setFont("helvetica", "normal");
  
      servicosSelecionados.forEach(el => {
        const nome = el.value;
        const dias = el.dataset.dias;
        const valor = parseFloat(el.dataset.valor).toLocaleString("pt-BR", {
          style: "currency",
          currency: "BRL"
        });
        const complexidade = el.dataset.complexidade;
        const descricao = el.dataset.descricao;
  
        novaPaginaSeNecessario(20);
        doc.text(doc.splitTextToSize(nome, 85), 12, y);
        doc.text(dias.toString(), 100, y);
        doc.text(valor, 150, y);
        y += 6;
  
        doc.setFontSize(10);
        doc.setTextColor(100);
        doc.text(`Complexidade: ${complexidade}`, 12, y);
        y += 5;
  
        const descricaoFormatada = doc.splitTextToSize(descricao, 180);
        descricaoFormatada.forEach(linha => {
          novaPaginaSeNecessario(8);
          doc.text(linha, 12, y);
          y += 4;
        });
  
        doc.setFontSize(12);
        doc.setTextColor(0);
        y += 4;
      });
    } else {
      doc.setTextColor(200, 0, 0);
      doc.setFont("helvetica", "normal");
      doc.text("Nenhum serviço selecionado.", 14, y);
      y += 6;
    }
  
    y += 10;
  
    const resultadoTexto = document.getElementById("resultado")?.innerText.trim() || "R$ 0,00";
    const linhasResultado = resultadoTexto.split("\n");
  
    doc.setFont("helvetica", "bold");
    doc.setFontSize(14);
    doc.setTextColor(231, 76, 60);
  
    linhasResultado.forEach(linha => {
      novaPaginaSeNecessario(10);
      doc.text(linha.trim(), 10, y);
      y += 8;
    });
  
    y += 10;
  
    const textoLink = "Clique aqui e inicie a contratação";
    const url = "https://api.whatsapp.com/send?phone=5551992671278&text=Oi%2C%20fiz%20um%20or%C3%A7amento%20e%20quero%20contratar%20os%20servi%C3%A7os.";
  
    doc.setTextColor(0, 0, 200);
    doc.setFont("helvetica", "bolditalic");
    doc.setFontSize(11);
    novaPaginaSeNecessario(10);
    doc.textWithLink(textoLink, 10, y, { url });
    y += 8;
  
    doc.setFont("helvetica", "italic");
    doc.setFontSize(10);
    doc.setTextColor(100);
    novaPaginaSeNecessario(10);
    doc.text(`Gerado automaticamente em: ${dataAtual}`, 10, y);
  
    doc.save("orcamento-dan-schaidt.pdf");
  });
  